def get_three_multi(A, target):
    if len(A) < 3:
        return 0
    count = 0
    count_dict = {}
    count_dict[A[0] + A[1]] = 1
    for j in range(2, len(A)):
        count += count_dict[target - A[j]] if target-A[j] in count_dict else 0
        for i in range(0, j):
            num_sum = A[i] + A[j]
            count_dict[num_sum] = 1 if num_sum not in count_dict else count_dict[num_sum] + 1
    return count % (10**9 + 7)

